<script type="text/javascript">
  function colocar(e,id)
  {
    if (e.keyCode==13)
    {
    if (id.substr(1,1)=='#')
    {
      letra='ABCDEFGHIJKLMNOPQRSTUVWXYZ';
      $('facorrelat_lonart').value=id.length;
    val=id.split("-");
    rup=parseInt(val.length);
    $('facorrelat_rupart').value=rup;

    j=0;
    res="";
