      val[j]=val[j].replace('#',letra.substr(j,1));
      val[j]=val[j].replace('#',letra.substr(j,1));
      val[j]=val[j].replace('#',letra.substr(j,1));
      val[j]=val[j].replace('#',letra.substr(j,1));
      res=res+val[j];
    }
    else
    {
      val[j]=val[j].replace('#',letra.substr(j,1));
      val[j]=val[j].replace('#',letra.substr(j,1));
      val[j]=val[j].replace('#',letra.substr(j,1));
      val[j]=val[j].replace('#',letra.substr(j,1));
      val[j]=val[j].replace('#',letra.substr(j,1));
      val[j]=val[j].replace('#',letra.substr(j,1));
      val[j]=val[j].replace('#',letra.substr(j,1));
      val[j]=val[j].replace('#',letra.substr(j,1));
      val[j]=val[j].replace('#',letra.substr(j,1));
      val[j]=val[j].replace('#',letra.substr(j,1));
      val[j]=val[j].replace('#',letra.substr(j,1));
      res=res+"-"+val[j];
    }
    j++;
    }
    $('facorrelat_desart').value=res;
    }
    else
    {
      alert('El Caracter que esta usando es errado, debe el mismo debe ser #');
    }
   }
  }

